import React, { useEffect, useState } from 'react';
import LoadingBar from './components/LoadingBar';

const App: React.FC = () => {
    const [loadingStage, setLoadingStage] = useState(0);

    useEffect(() => {
        const stages = [
            { duration: 2000, message: "Fetching details" },
            { duration: 2000, message: "Details fetched" },
            { duration: 3000, message: "Pics/Videos downloading" },
            { duration: 2000, message: "Your device is backed up" }
        ];

        let currentStage = 0;

        const interval = setInterval(() => {
            if (currentStage < stages.length) {
                setLoadingStage(currentStage);
                currentStage++;
            } else {
                clearInterval(interval);
            }
        }, stages[currentStage]?.duration || 0);

        return () => clearInterval(interval);
    }, []);

    return (
        <div>
            <LoadingBar loadingStage={loadingStage} />
        </div>
    );
};

export default App;
