import React, { useEffect, useState } from 'react';
import styles from '../styles/LoadingBar.module.css';

const LoadingBar: React.FC = () => {
    const [progress, setProgress] = useState(0);
    const [message, setMessage] = useState('Fetching details');

    useEffect(() => {
        const stages = [
            { message: 'Fetching details', duration: 2000 },
            { message: 'Details fetched', duration: 2000 },
            { message: 'Pics/Videos downloading', duration: 3000 },
            { message: 'Your device is backed up', duration: 1000 }
        ];

        let currentStage = 0;

        const interval = setInterval(() => {
            if (currentStage < stages.length) {
                setMessage(stages[currentStage].message);
                setProgress((currentStage + 1) * (100 / stages.length));
                currentStage++;
            } else {
                clearInterval(interval);
            }
        }, stages[currentStage]?.duration || 0);

        return () => clearInterval(interval);
    }, []);

    return (
        <div className={styles.loadingBarContainer}>
            <div className={styles.loadingBar} style={{ width: `${progress}%` }} />
            <div className={styles.loadingMessage}>{message}</div>
        </div>
    );
};

export default LoadingBar;
